INSERT INTO `wp_postmeta` VALUES("621", "205", "_leadership_2_title", "field_55b94b7d97427");
INSERT INTO `wp_postmeta` VALUES("622", "205", "leadership_2_bio", "Linda Murphy has direct responsibility for oversite of the care management units serving all My Choice Members.She provides direction for Enrollment and Eligibility as well as the Best Practice Team. Ms. Murphy has her Master of Social Work degree from the University of Wisconsin - Milwaukee");
INSERT INTO `wp_postmeta` VALUES("623", "205", "_leadership_2_bio", "field_55b94ba197428");
INSERT INTO `wp_postmeta` VALUES("624", "205", "leadership_2_headshot", "26");
INSERT INTO `wp_postmeta` VALUES("625", "205", "_leadership_2_headshot", "field_55b94bd097429");
INSERT INTO `wp_postmeta` VALUES("626", "205", "leadership_3_name", "Jim Hodson");
INSERT INTO `wp_postmeta` VALUES("627", "205", "_leadership_3_name", "field_55b94b4897426");
INSERT INTO `wp_postmeta` VALUES("628", "205", "leadership_3_title", "Chief Financial Officer");
INSERT INTO `wp_postmeta` VALUES("629", "205", "_leadership_3_title", "field_55b94b7d97427");
INSERT INTO `wp_postmeta` VALUES("630", "205", "leadership_3_bio", "Jim Hodson is a Certified Public Accountant with 25 years of financial management experience in long-term and managed care working for private, nonprofit and public accounting organizations. Mr. Hodson provides My Choice Family Care with comprehensive financial leadership in directing accounting, finance and contracting operations to achieve its goals and objectives, including: strategic planning and forecasting; business development; financial management; systems development; and administrative functions.");
INSERT INTO `wp_postmeta` VALUES("631", "205", "_leadership_3_bio", "field_55b94ba197428");
