INSERT INTO `wp_postmeta` VALUES("636", "205", "philosophy_headline", "Our Philosophy");
INSERT INTO `wp_postmeta` VALUES("637", "205", "_philosophy_headline", "field_55ba2ff6e2f10");
INSERT INTO `wp_postmeta` VALUES("638", "205", "philosophy_text", "My Choice Family Care is committed to working together with Members, families, advocates, friends and others in a spirit that:\r\n<ol>\r\n	<li>Promotes respect and dignity</li>\r\n	<li>Supports choices of our Members</li>\r\n	<li>Informs Members about the benefits of their choices</li>\r\n	<li>Promotes member participation</li>\r\n	<li>Uses cost-effective methods and</li>\r\n	<li>Works within government policies and regulations</li>\r\n</ol>\r\n<h4></h4>");
INSERT INTO `wp_postmeta` VALUES("639", "205", "_philosophy_text", "field_55ba3076e2f11");
INSERT INTO `wp_postmeta` VALUES("640", "32", "philosophy_headline", "Our Philosophy");
INSERT INTO `wp_postmeta` VALUES("641", "32", "_philosophy_headline", "field_55ba2ff6e2f10");
INSERT INTO `wp_postmeta` VALUES("642", "32", "philosophy_text", "My Choice Family Care is committed to working together with Members, families, advocates, friends and others in a spirit that:\r\n<ol>\r\n	<li>Promotes respect and dignity</li>\r\n	<li>Supports choices of our Members</li>\r\n	<li>Informs Members about the benefits of their choices</li>\r\n	<li>Promotes member participation</li>\r\n	<li>Uses cost-effective methods and</li>\r\n	<li>Works within government policies and regulations</li>\r\n</ol>\r\n<h4></h4>");
INSERT INTO `wp_postmeta` VALUES("643", "32", "_philosophy_text", "field_55ba3076e2f11");
INSERT INTO `wp_postmeta` VALUES("644", "32", "mission_healine", "Our Mission Statement");
INSERT INTO `wp_postmeta` VALUES("645", "32", "_mission_healine", "field_55ba31656492b");
INSERT INTO `wp_postmeta` VALUES("646", "32", "mission_text", "My Choice Family Care respects the dignity and personal autonomy of each Member by honoring choice and providing a high quality, cost-effective services and supports");
INSERT INTO `wp_postmeta` VALUES("647", "32", "_mission_text", "field_55ba31866492c");
INSERT INTO `wp_postmeta` VALUES("648", "32", "values_headline", "Our Values");
INSERT INTO `wp_postmeta` VALUES("649", "32", "_values_headline", "field_55ba31a16492d");
INSERT INTO `wp_postmeta` VALUES("650", "32", "values_text", "Our values guide the development of our mission and our work. We will always put the best interest of our Members at the center of decision making.\r\n\r\nOur Values include:\r\n<ol>\r\n	<li><b>Care that embraces the whole person</b> — We are committed to providing quality care for body, mind and spirit and to preserving quality of life for our Members.</li>\r\n	<li><b>Dignity</b> — We respect the dignity and personal choices of each member.</li>\r\n	<li><b>Collaboration</b> — We believe in teamwork that includes our Members,their families, support systems and other organizations.</li>\r\n	<li><b>Justice</b> — We believe that all Members must receive fair treatment that is free from discrimination.</li>\r\n	<li><b>Stewardship</b> — We have concern for the careful use of our resources and we value public opinion.</li>\r\n	<li><b>Quality</b> — We will strive to provide the best care for our Members.</li>\r\n	<li><b>Cultural Competence</b> — We honor the cultural values of our Members</li>\r\n</ol>");
INSERT INTO `wp_postmeta` VALUES("651", "32", "_values_text", "field_55ba31f76492e");
INSERT INTO `wp_postmeta` VALUES("652", "32", "governing_board_headline", "Governing Board");
INSERT INTO `wp_postmeta` VALUES("653", "32", "_governing_board_headline", "field_55ba336ae0db4");
INSERT INTO `wp_postmeta` VALUES("654", "32", "governing_board_text", "My Choice Family Care (MCFC) Managed Care Organization (MCO) meets all statutory requirements for a Family Care Governing Board, Wis. Stats. s.46.284 (6), including having a board that reflects the ethics and economic diversity of the geographic area served by the care management organization.\r\n\r\nThe Governing Board meets every other month. All Governing Board Meetings are open to the Public. Membership of the My Choice Family Care Governing Board exceeds the requirement that at least one fourth of the members be representative of the client group or groups whom the managed care organization is contracted to serve or those client’s family members, guardians, or other advocates.");
INSERT INTO `wp_postmeta` VALUES("655", "32", "_governing_board_text", "field_55ba3383e0db5");
INSERT INTO `wp_postmeta` VALUES("656", "212", "_edit_last", "1");
INSERT INTO `wp_postmeta` VALUES("657", "212", "_edit_lock", "1438283684:1");
INSERT INTO `wp_postmeta` VALUES("658", "215", "my_choice_headline", "My Choice Family Care");
INSERT INTO `wp_postmeta` VALUES("659", "215", "_my_choice_headline", "field_55ba35eaaf64a");
INSERT INTO `wp_postmeta` VALUES("660", "215", "my_choice_text", "Welcome to My Choice Family Care’s website – your direct connection to information about the Managed Care Organization and the Family Care Program. My Choice Family Care is your source for Long-Term Support You Can Trust! Since 2000, we have provided the Family Care program to over 21,720 people in Milwaukee County. Today, we serve over 8,300 members. We are committed to helping the people in our community remain as independent as possible.\r\n\r\nWe believe that if you need long-term care services, you should be able to get them when you need them and at a place you choose. Our goal is to work with you to design a plan to provide you with cost-effective care that meets your outcomes. We are a financially strong organization that strives to ensure that our members receive services that are delivered the Right Way for each Person – at the Right Time– in the Right Place – for the Right Cost and for the Right Reasons. View the video below to see how My Choice Family Care works with you and your family to be creative and keep you as independent as possible.");
INSERT INTO `wp_postmeta` VALUES("661", "215", "_my_choice_text", "field_55ba3613af64b");
INSERT INTO `wp_postmeta` VALUES("662", "11", "my_choice_headline", "My Choice Family Care");
INSERT INTO `wp_postmeta` VALUES("663", "11", "_my_choice_headline", "field_55ba35eaaf64a");
INSERT INTO `wp_postmeta` VALUES("664", "11", "my_choice_text", "Welcome to My Choice Family Care’s website – your direct connection to information about the Managed Care Organization and the Family Care Program. My Choice Family Care is your source for Long-Term Support You Can Trust! Since 2000, we have provided the Family Care program to over 21,720 people in Milwaukee County. Today, we serve over 8,300 members. We are committed to helping the people in our community remain as independent as possible.\r\n\r\nWe believe that if you need long-term care services, you should be able to get them when you need them and at a place you choose. Our goal is to work with you to design a plan to provide you with cost-effective care that meets your outcomes. We are a financially strong organization that strives to ensure that our members receive services that are delivered the Right Way for each Person – at the Right Time– in the Right Place – for the Right Cost and for the Right Reasons. View the video below to see how My Choice Family Care works with you and your family to be creative and keep you as independent as possible.");
INSERT INTO `wp_postmeta` VALUES("665", "11", "_my_choice_text", "field_55ba3613af64b");
INSERT INTO `wp_postmeta` VALUES("666", "11", "entroll_headline", "Enroll In My Choice");
INSERT INTO `wp_postmeta` VALUES("667", "11", "_entroll_headline", "field_55ba37f505719");
INSERT INTO `wp_postmeta` VALUES("668", "11", "enroll_text", "To be eligible for the Family Care program with the My Choice Family Care, a person must have long-term care service needs, be an older adult or an adult with a disability, live within our service area and meet financial eligibility requirements.\r\n    \r\nIf you or someone you know is interested in learning more about the Family Care program, the first step is to contact the appropriate resource center. Resource Centers provide information and assistance and help people apply for programs and benefits. They are available to help you, whether or not you decide to become a Family Care Member.\r\n\r\nClick on the map to the right to find a resource center in your county");
INSERT INTO `wp_postmeta` VALUES("669", "11", "_enroll_text", "field_55ba38090571a");
