INSERT INTO `wp_postmeta` VALUES("858", "34", "members_description_col_1", "My Choice Family Care is your source for <b>Long-Term Support You Can Trust!</b> Since 2000, we have provided the Family Care program to over 21,720 people in Milwaukee County. Today, we serve over 8,300 members.\r\n\r\nWe are committed to helping the people in our community remain as independent as possible.We believe that if you need long-term care services, you should be able to get them when you need them and at a place you choose.");
INSERT INTO `wp_postmeta` VALUES("859", "34", "_members_description_col_1", "field_55ba5d619c3f1");
INSERT INTO `wp_postmeta` VALUES("860", "34", "members_description_col_2", "Our goal is to work with you to design a plan to provide you with cost-effective care that meets your outcomes. We are a financially strong organization that strives to ensure that our members receive services that are delivered the Right Way for each Person – at the Right Time – in the Right Place – for the Right Cost and for the Right Reasons. View movie below to see how My Choice Family Care works with you and your family to be creative and keep you as independent as possible.");
INSERT INTO `wp_postmeta` VALUES("861", "34", "_members_description_col_2", "field_55ba5d979c3f2");
INSERT INTO `wp_postmeta` VALUES("862", "251", "members_description_col_1", "My Choice Family Care is your source for <b>Long-Term Support You Can Trust!</b> Since 2000, we have provided the Family Care program to over 21,720 people in Milwaukee County. Today, we serve over 8,300 members.\r\n\r\nWe are committed to helping the people in our community remain as independent as possible.We believe that if you need long-term care services, you should be able to get them when you need them and at a place you choose.");
