INSERT INTO `wp_posts` VALUES("201", "1", "2015-07-29 22:22:30", "2015-07-29 22:22:30", "a:12:{s:4:\"type\";s:4:\"text\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:7:\"History\";s:11:\"placeholder\";s:0:\"\";s:7:\"prepend\";s:0:\"\";s:6:\"append\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:8:\"readonly\";i:0;s:8:\"disabled\";i:0;}", "History Headline", "history_headline", "publish", "open", "open", "", "field_55b9519dd2163", "", "", "2015-07-30 14:13:34", "2015-07-30 14:13:34", "", "171", "http://mychoice.localhost/?post_type=acf-field&#038;p=201", "0", "acf-field", "", "0");
INSERT INTO `wp_posts` VALUES("202", "1", "2015-07-29 22:22:30", "2015-07-29 22:22:30", "a:12:{s:4:\"type\";s:8:\"textarea\";s:12:\"instructions\";s:32:\"Put the history description here\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:1051:\"Many people need assistance with activities of daily living and caring for their health due to a physical, intellectual or developmental disability or because of the increasing challenges that come with aging. Most long-term care is actually provided by family members, and many people pay privately for the care they receive. However there are many Wisconsin residents who need assistance and who cannot afford to pay for their care on their own.\r\n\r\nIn 2000, the State of Wisconsin launched the Family Care program as a pilot program in 5 Wisconsin counties. Milwaukee County was one of the original 5 pilot counties.\r\n\r\nNow serving over 8,400 members in eight Wisconsin counties, the MCO has strengthened its commitment to the communities it serves and is now known as My Choice Family Care. A leader in providing the Family Care program in Wisconsin, My Choice Family Care is a robust and thriving operation now celebrating 15 years of successfully serving more than 21,722 Family Care members. Read more about the history of My Choice Family Care.\";s:11:\"placeholder\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:4:\"rows\";s:0:\"\";s:9:\"new_lines\";s:7:\"wpautop\";s:8:\"readonly\";i:0;s:8:\"disabled\";i:0;}", "History Description", "history_description", "publish", "open", "open", "", "field_55b951b6d2164", "", "", "2015-07-30 14:13:34", "2015-07-30 14:13:34", "", "171", "http://mychoice.localhost/?post_type=acf-field&#038;p=202", "1", "acf-field", "", "0");
INSERT INTO `wp_posts` VALUES("203", "1", "2015-07-30 14:13:34", "2015-07-30 14:13:34", "a:12:{s:4:\"type\";s:4:\"text\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:14:\"Our Philosophy\";s:11:\"placeholder\";s:0:\"\";s:7:\"prepend\";s:0:\"\";s:6:\"append\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:8:\"readonly\";i:0;s:8:\"disabled\";i:0;}", "Philosophy Headline", "philosophy_headline", "publish", "open", "open", "", "field_55ba2ff6e2f10", "", "", "2015-07-30 14:23:36", "2015-07-30 14:23:36", "", "171", "http://mychoice.localhost/?post_type=acf-field&#038;p=203", "4", "acf-field", "", "0");
INSERT INTO `wp_posts` VALUES("204", "1", "2015-07-30 14:13:34", "2015-07-30 14:13:34", "a:9:{s:4:\"type\";s:7:\"wysiwyg\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:788:\"<p>\r\n              My Choice Family Care is committed to working together with Members, families, advocates, friends and others in a spirit that:</p>\r\n            <ol>\r\n              <li>Promotes respect and dignity </li>\r\n              <li>Supports choices of our Members</li>\r\n              <li>Informs Members about the benefits of their choices </li>\r\n              <li>Promotes member participation</li>\r\n              <li>Uses cost-effective methods and</li>\r\n              <li>Works within government policies and regulations</li>\r\n            </ol>\r\n            <h4>Our Mission Statement </h4>\r\n            <p>My Choice Family Care respects the dignity and personal autonomy of each Member by honoring choice and providing a high quality, cost-effective services and supports.</p>\";s:4:\"tabs\";s:3:\"all\";s:7:\"toolbar\";s:4:\"full\";s:12:\"media_upload\";i:1;}", "Philosophy Text", "philosophy_text", "publish", "open", "open", "", "field_55ba3076e2f11", "", "", "2015-07-30 14:23:36", "2015-07-30 14:23:36", "", "171", "http://mychoice.localhost/?post_type=acf-field&#038;p=204", "5", "acf-field", "", "0");
