INSERT INTO `wp_posts` VALUES("209", "1", "2015-07-30 14:19:00", "2015-07-30 14:19:00", "a:9:{s:4:\"type\";s:7:\"wysiwyg\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:1146:\"<p>Our values guide the development of our mission and our work. We will always put the best interest of our Members at the center of decision making.</p>\r\n            <p> Our Values include:</p>\r\n            <ol>\r\n              <li><b>Care that embraces the whole person</b> — We are committed to providing quality care for body, mind and spirit and to preserving quality of life for our Members.</li>\r\n              <li><b>Dignity</b> — We respect the dignity and personal choices of each member. </li>\r\n              <li><b>Collaboration</b> — We believe in teamwork that includes our Members,their families, support systems and other organizations.</li>\r\n              <li><b>Justice</b> — We believe that all Members must receive fair treatment that is free from discrimination. </li>\r\n              <li><b>Stewardship</b> — We have concern for the careful use of our resources and we value public opinion. </li>\r\n              <li><b>Quality</b> — We will strive to provide the best care for our Members. </li>\r\n              <li><b>Cultural Competence</b> — We honor the cultural values of our Members</li>\r\n            </ol>\";s:4:\"tabs\";s:3:\"all\";s:7:\"toolbar\";s:4:\"full\";s:12:\"media_upload\";i:1;}", "Values Text", "values_text", "publish", "open", "open", "", "field_55ba31f76492e", "", "", "2015-07-30 14:23:36", "2015-07-30 14:23:36", "", "171", "http://mychoice.localhost/?post_type=acf-field&#038;p=209", "3", "acf-field", "", "0");
INSERT INTO `wp_posts` VALUES("210", "1", "2015-07-30 14:25:53", "2015-07-30 14:25:53", "a:12:{s:4:\"type\";s:4:\"text\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:15:\"Governing Board\";s:11:\"placeholder\";s:0:\"\";s:7:\"prepend\";s:0:\"\";s:6:\"append\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:8:\"readonly\";i:0;s:8:\"disabled\";i:0;}", "Governing Board Headline", "governing_board_headline", "publish", "open", "open", "", "field_55ba336ae0db4", "", "", "2015-07-30 14:25:53", "2015-07-30 14:25:53", "", "171", "http://mychoice.localhost/?post_type=acf-field&p=210", "9", "acf-field", "", "0");
INSERT INTO `wp_posts` VALUES("211", "1", "2015-07-30 14:25:53", "2015-07-30 14:25:53", "a:12:{s:4:\"type\";s:8:\"textarea\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:688:\"My Choice Family Care (MCFC) Managed Care Organization (MCO) meets all statutory requirements for a Family Care Governing Board, Wis. Stats. s.46.284 (6), including having a board that reflects the ethics and economic diversity of the geographic area served by the care management organization.\r\n\r\nThe Governing Board meets every other month. All Governing Board Meetings are open to the Public. Membership of the My Choice Family Care Governing Board exceeds the requirement that at least one fourth of the members be representative of the client group or groups whom the managed care organization is contracted to serve or those client’s family members, guardians, or other advocates.\";s:11:\"placeholder\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:4:\"rows\";s:0:\"\";s:9:\"new_lines\";s:7:\"wpautop\";s:8:\"readonly\";i:0;s:8:\"disabled\";i:0;}", "Governing Board Text", "governing_board_text", "publish", "open", "open", "", "field_55ba3383e0db5", "", "", "2015-07-30 14:25:53", "2015-07-30 14:25:53", "", "171", "http://mychoice.localhost/?post_type=acf-field&p=211", "10", "acf-field", "", "0");
