INSERT INTO `wp_posts` VALUES("217", "1", "2015-07-30 14:46:06", "2015-07-30 14:46:06", "a:12:{s:4:\"type\";s:8:\"textarea\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:669:\"To be eligible for the Family Care program with the My Choice Family Care, a person must have long-term care service needs, be an older adult or an adult with a disability, live within our service area and meet financial eligibility requirements.\r\n    \r\nIf you or someone you know is interested in learning more about the Family Care program, the first step is to contact the appropriate resource center. Resource Centers provide information and assistance and help people apply for programs and benefits. They are available to help you, whether or not you decide to become a Family Care Member.\r\n\r\nClick on the map to the right to find a resource center in your county\";s:11:\"placeholder\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:4:\"rows\";s:0:\"\";s:9:\"new_lines\";s:7:\"wpautop\";s:8:\"readonly\";i:0;s:8:\"disabled\";i:0;}", "Enroll Text", "enroll_text", "publish", "open", "open", "", "field_55ba38090571a", "", "", "2015-07-30 14:46:06", "2015-07-30 14:46:06", "", "212", "http://mychoice.localhost/?post_type=acf-field&p=217", "3", "acf-field", "", "0");
