INSERT INTO `wp_posts` VALUES("248", "1", "2015-07-30 17:24:35", "2015-07-30 17:24:35", "a:9:{s:4:\"type\";s:7:\"wysiwyg\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:471:\"<p>My Choice Family Care is your source for <b>Long-Term Support You Can Trust!</b> Since 2000, we have provided the Family Care program to over 21,720 people in Milwaukee County. Today, we serve over 8,300 members.</p>\r\n          <p>\r\n            We are committed to helping the people in our community remain as independent as possible.We believe that if you need long-term care services, you should be able to get them when you need them and at a place you choose.</p>\";s:4:\"tabs\";s:3:\"all\";s:7:\"toolbar\";s:4:\"full\";s:12:\"media_upload\";i:1;}", "Members Description Col 1", "members_description_col_1", "publish", "open", "open", "", "field_55ba5d619c3f1", "", "", "2015-07-30 18:58:47", "2015-07-30 18:58:47", "", "176", "http://mychoice.localhost/?post_type=acf-field&#038;p=248", "0", "acf-field", "", "0");
